sqlMap.select:
/* CenterMgmt.retrieveCenterList */ SELECT code, name, chief, address, phone FROM UIP_CENTER WHERE code != '00000'
code = #code#
order by code, name

sqlMap.delete:
 /* CenterMgmt.deleteCenter */ 
DELETE FROM UIP_CENTER
WHERE code = #code#

 /* CenterMgmt.deleteAllRegion */ 
DELETE FROM UIP_REGION
WHERE code = #code#
