sqlMap.update:
/* loginXP.changePwd */ UPDATE SY_USER SET , PWD = #pwd# , FNL_EDIT_USER_ID = #fnlEditUserId# , FNL_EDIT_DT = SYSDATE
, OGG_TIME = TO_TIMESTAMP(#oggTime#, 'YYYYMMDDHH24MISSFF') WHERE 1 = 1
USER_ID = #userId#

sqlMap.select:
/* loginXP.retrieveDeptList */ SELECT A.CPR_CODE , A.DEPT_CODE , A.DEPT_NM , A.UPPER_DEPT_CODE AS UPPER_DEPT_CODE , A.UPPER_DEPT_CODE AS CENTER_ORG_CD , A.CT_DEPT_CODE , A.BUDGET_DEPT_CODE , A.PRFLOS_CNTER_CODE , A.BSNS_RELM_CODE , A.ORG_CD FROM V_SY_DEPT A WHERE 1=1
A.DEPT_CODE = #deptCode#
A.DEPT_NM LIKE '%'||#deptNm#||'%'

/* loginXP.topMenuList */ SELECT AA.DUTY_SYS_CD , AA.MENU_ID , AA.MENU_NM , AA.MENU_EXPL , AA.MENU_LVL , AA.MENU_CD , AA.MENU_URL , AA.MENU_VAR , AA.HGR_MENU_ID , AA.ARRAY_ORD , AA.QRY_YN , AA.POPUP_YN , AA.MENU_HPWRD_FILE_NM FROM
(SELECT DISTINCT DUTY_SYS_CD , A.MENU_ID , A.MENU_NM , A.MENU_EXPL , A.MENU_CD , A.MENU_LVL , A.MENU_URL , A.MENU_VAR , A.HGR_MENU_ID , A.ARRAY_ORD , A.QRY_YN , A.POPUP_YN , A.MENU_HPWRD_FILE_NM FROM SY_MENU A WHERE A.MENU_LVL = 1 AND A.USE_YN = 'Y' AND A.QRY_YN = 'Y')
(SELECT DISTINCT DUTY_SYS_CD , A.MENU_ID , A.MENU_NM , A.MENU_EXPL , A.MENU_LVL , A.MENU_CD , A.MENU_URL , A.MENU_VAR , A.HGR_MENU_ID , A.ARRAY_ORD , A.QRY_YN , A.POPUP_YN , A.MENU_HPWRD_FILE_NM FROM SY_MENU A INNER JOIN SY_PRIV_CTG_MENU B ON (A.MENU_ID = B.MENU_ID) WHERE A.MENU_LVL = 1 AND A.USE_YN = 'Y' AND A.QRY_YN = 'Y' AND B.SYS_CD = #sysCd#
#privCd[]#
)
AA ORDER BY ARRAY_ORD
